import {
  PieceType,
  TeamType,
  Piece,
} from "../components/Chessboard/Chessboard";

export default class Referee {
  tileIsOccupied(x, y, boardState) {
    const piece = boardState.find((p) => p.x === x && p.y === y);
    if (piece) {
      return true;
    } else {
      return false;
    }
  }

  isValidMove(px, py, x, y, type, team, boardState) {
    if (type === PieceType[0]) {
      if (team === TeamType[1]) {
        if (py === 1) {
          if (px === x && y - py === 1) {
            if (!this.tileIsOccupied(x, y, boardState)) {
              return true;
            }
          } else if (px === x && y - py === 2) {
            if (!this.tileIsOccupied(x, y - 1, boardState)) {
              return true;
            }
          }
        } else {
          if (px === x && y - py === 1) {
            if (
              !this.tileIsOccupied(x, y, boardState) &&
              !this.tileIsOccupied(x, y - 1, boardState)
            ) {
              return true;
            }
          }
        }
      } else {
        if (py === 6) {
          if (px === x && y - py === -1) {
            if (!this.tileIsOccupied(x, y, boardState)) {
              return true;
            }
          } else if (px === x && y - py === -2) {
          }
        } else {
          if (px === x && y - py === -1) {
            return true;
          }
        }
      }
    }

    return false;
  }
}
